definition.script:
pipeline {
    agent any

    stages {
        stage('Checkout') {
            steps {
                script {
                    // Print working directory
                    sh 'echo "Current directory: $(pwd)"'
                }

                // Clonning the GitHub repo
                git url: 'https://github.com/andaniel1995/python_database_app.git', branch: 'master'
            }
        }

        stage('Build and Deploy') {
            steps {
                script {
                    // Terminate any active containers then build and redeploy new ones with the latest version from GitHub
                    sh 'docker-compose down'
                    sh 'docker-compose up -d --build'
                }
            }
        }
    }
}
